import React,{Component} from 'react';
import {storeProducts,detailProduct} from './data';
const ProductContext = React.createContext();

class ProductProvider extends React.Component {
  state = {
    products:[],
    detailProduct:detailProduct,
    cart:[],
    modalOpen:false,
    modalProduct:detailProduct,
    cartSubtotal:0,
    cartTax:0,
    cartTotal:0
  }

  componentDidMount(){
    this.setProducts();
  }

  setProducts = ()=> {
    let temProducts = [];
    storeProducts.forEach(item => {
      const singleItem = {...item};
      temProducts = [...temProducts,singleItem];
    });
    this.setState(()=>{
      return {products:temProducts};
    }); 
   }

  getItem = (id) => {
    const product = this.state.products.find(item=>item.id===id);
    console.log('Hi, kjsdkfsjd',product);
    return product;
  }

  handleDetail = (id) => {
    const product = this.getItem(id);
    this.setState(() => {
    return{
      detailProduct:product
    };
    });

  }

  addToCart = (id) => {
    let temProducts = [...this.state.products];
    let index = temProducts.indexOf(this.getItem(id));
    const product = temProducts[index];
    product.inCart = true;
    product.count = 1;
    const price = product.price;
    product.total = price;
    this.setState(()=>{
      return {
        products:temProducts,cart:[...this.state.cart,product]
      }
    },()=>{ console.log('data',this.state) });

  }

  openModal = id =>{
    const product = this.getItem(id);
    this.setState(()=>{
      return{
        modalProduct:product,modalOpen:true
      }
    })
  }
  closeModal = () => {
   this.setState(()=>{
     return{
       modalOpen:false
     }
   });
  }

  increment = (id) =>{
   console.log(`this is increment method ${id}`);
  }
  increment = (id) =>{
    console.log(`this is increment method ${id}`);
   }

  render(){
    return(
      <ProductContext.Provider value={{...this.state,
      handleDetail:this.handleDetail,
      addToCart:this.addToCart,
      modalOpen:this.openModal,
      closeModal:this.closeModal
      }}>
        {this.props.children}
      </ProductContext.Provider>
    );
  }
}
const ProductConsumer = ProductContext.Consumer
export {ProductProvider,ProductConsumer};
