import React,{Component} from 'react';

const CartItem = ({item,value}) => {

  const {id,title,img,price,total,count} = item;
  const {increment,decrement,removeItem} = value;
  console.log('its image url',img);

  return(
    <div className="row my-1 text-capitalize text-center">
      <div className="col-10 max-auto col-lg-2">
       <img src={img} style={{width:'5rem', height:'5rem'}}  className="img-fluid" alt="Product" />
      </div>
      <div className="col-10 max-auto col-lg-2">
        <span className="d-lg-none">
          product:
        </span>
        {title}
      </div>
      <div className="col-10 max-auto col-lg-2">
        <span className="d-lg-none">
          price:
        </span>
        {price}
      </div>
      <div className="col-10 max-auto col-lg-2 my-2 my-lg-0">
       <div className="d-flex justify-content-center">
        <div>
          <span className="btn btn-black max-1" onClick={()=>decrement(id)}>-</span>
          <span className="btn btn-black mx-1">{count}</span>
          <span className="btn btn-black max-1" onClick={()=>increment(id)}>+</span>
        </div>
       </div>
      </div>
      <div className="col-10 max-auto col-lg-2">

      </div>
      <div className="col-10 max-auto col-lg-2">
        <strong>
          item total: $ {total}
        </strong>
      </div>
     </div>
  );
}
export default CartItem;
